import React, { useState, useEffect } from "react"
import MovieDataService from "../services/movie"
import { Link } from "react-router-dom"

const MoviesList = props => {
    const [movies, setMovies] = useState([]);
    const [searchTitle, setSearchTitle] = useState("");
    const [searchGenre, setSearchGenre] = useState("");
    const [searchDirector, setSearchDirector] = useState("");
    const [directors, setDirectors] = useState(["All Directors"]);

    useEffect(() => {
        retrieveMovies();
        retrieveGenres();
        retrieveDirectors();
    }, []);

    const onChangeSearchTitle = e => {
        const searchTitle = e.target.value;
        setSearchTitle(searchTitle);
    };

    const onChangeSearchGenre = e => {
        const searchGenre = e.target.value;
        setSearchGenre(searchGenre);
    };

    const onChangeSearchDirector = e => {
        const searchDirector = e.target.value;
        setSearchDirector(searchDirector);

    };

    const retrieveMovies = () => {
        MovieDataService.getAll()
            .then(response => {
                console.log(response.data);
                setMovies(response.data.movies);

            })
            .catch(e => {
                console.log(e);
            });
    };

    const retrieveGenres = () => {
        MovieDataService.getGenres()
            .then(response => {
                console.log(response.data);
                setGenres(["All Genres"].concat(response.data));

            })
            .catch(e => {
                console.log(e);
            });
    };

    const retrieveDirectors= () => {
        MovieDataService.getDirectors()
            .then(response => {
                console.log(response.data);
                setDirectors(["All Directors"].concat(response.data));

            })
            .catch(e => {
                console.log(e);
            });
    };

    const refreshList = () => {
        retrieveMovies();
    };

    const find = (query, by) => {
        MovieDataService.find(query, by)
            .then(response => {
                console.log(response.data);
                setMovies(response.data.movies);
            })
            .catch(e => {
                console.log(e);
            });
    };

    const findByTitle = () => {
        find(searchTitle, "title")
    };

    const findByGenre = () => {
        find(searchGenre, "genre")
    };

    const findByDirector = () => {
        if (searchDirector == "All Cuisines") {
            refreshList();
        } else {
            find(searchDirector, "director")
        }
    };

    return (
        <div>
            <div className="row pb-1">
                <div className="input-group col-lg-4">
                    <input
                        type="text"
                        className="form-control"
                        placeholder="Search by title"
                        value={searchTitle}
                        onChange={onChangeSearchTitle}
                    />
                    <div className="input-group-append">
                        <button
                            className="btn btn-outline-secondary"
                            type="button"
                            onClick={findByTitle}
                        >
                            Search
                        </button>
                    </div>
                </div>

                <div className="input-group col-lg-4">
                    <input
                        type="text"
                        className="form-control"
                        placeholder="Search by genre"
                        value={searchGenre}
                        onChange={onChangeSearchGenre}
                    />
                    <div className="input-group-append">
                        <button
                            className="btn btn-outline-secondary"
                            type="button"
                            onClick={findByGenre}
                        >
                            Search
                        </button>
                    </div>
                </div>

                <div className="input-group col-lg-4">
                    <input
                        type="text"
                        className="form-control"
                        placeholder="Search by director"
                        value={searchDirector}
                        onChange={onChangeSearchDirector}
                    />
                    <div className="input-group-append">
                        <button
                            className="btn btn-outline-secondary"
                            type="button"
                            onClick={findByDirector}
                        >
                            Search
                        </button>
                    </div>
                </div>
            </div>
            {/* <div className="row">
                {movies.map((movie) => {
                    const address = `${restaurant.address.building} ${restaurant.address.street}, ${restaurant.address.zipcode}`;
                    return (
                        <div className="col-lg-4 pb-1">
                            <div className="card">
                                <div className="card-body">
                                    <h5 className="card-title">{restaurant.name}</h5>
                                    <p className="card-text">
                                        <strong>Cuisine: </strong>{restaurant.cuisine}<br />
                                        <strong>Address: </strong>{address}
                                    </p>
                                    <div className="row">
                                        <Link to={"/restaurants/" + restaurant._id} className="btn btn-primary col-lg-5 mx-1 mb-1">
                                            View Reviews
                                        </Link>
                                        <a target="_blank" href={"https://www.google.com/maps/place/" + address} className="btn btn-primary col-lg-5 mx-1 mb-1">View Map</a>
                                    </div>
                                </div>
                            </div>
                        </div>
                    );
                })}


            </div> */}
        </div>
    );
};

export default MoviesList;
